import React, { useState, useRef } from "react";
import { Link } from "react-router-dom";
import classes from "./SignupAuth.module.css";
import Card from "../../UI/Card/Card";
const Signup = (props) => {
  const nameInputRef = useRef();
  const userInputRef = useRef();
  const emailInputRef = useRef();
  const passwordInputRef = useRef();

  const [isLogin, setIsLogin] = useState(true);

  const switchAuthModeHandler = () => {
    setIsLogin((prevState) => !prevState);
  };
  const SubmitHandler = (event) => {
    event.preventDefault();
    const data = {};

    if (isLogin) {
    } else {
      fetch("http://stack-api-server.herokuapp.com/user/singup", {
        method: "POST",
        body: JSON.stringify({
          name: this.enteredName,
          username: this.enteredUser,
          email: this.enteredEmail,
          password: this.enteredPassword,
          Token: "XzUTxZYwXtELUmHNrzzJndRzmKuxjdnorCrnrVSEyKofG",
        }),
        headers: {
          "Content-Type": "application/json",
        },
      }).then((res) => {
        if (res.ok) {
        } else {
          return res.json().then((data) => {
            console.log(data);
          });
        }
      });
    }
  };
  return (
    <section className={classes.auth}>
      <h1>{isLogin ? "Login" : "Sign Up"}</h1>
      <form onSubmit={SubmitHandler}>
        <div className={classes.control}>
          <label htmlFor="text">Your Name</label>
          <input type="text" id="name" required ref={nameInputRef} />
        </div>
        <div className={classes.control}>
          <label htmlFor="username">Your Username</label>
          <input type="text" id="username" required ref={userInputRef} />
        </div>
        <div className={classes.control}>
          <label htmlFor="email">Your Email</label>
          <input type="email" id="email" required ref={emailInputRef} />
        </div>
        <div className={classes.control}>
          <label htmlFor="password">Your Password</label>
          <input
            type="password"
            id="password"
            required
            ref={passwordInputRef}
          />
        </div>
        <div className={classes.actions}>
          <button>{isLogin ? "Login" : "Create Account"}</button>
          <button
            type="button"
            className={classes.toggle}
            onClick={switchAuthModeHandler}
          >
            {isLogin ? "Create new account" : "Login with existing account"}
          </button>
        </div>
      </form>
    </section>
  );
};

export default Signup;
