import React, { useRef, useState, useEffect } from "react";
import { Link } from "react-router-dom";
import ContactList from "../ContactList/ContactList";
import "./RegisterPage.module.css";
const RegisterPage = (props) => {
  const userRef = useRef();
  const errRef = useRef();

  const [user, setUser] = useState("");
  const [password, setPassword] = useState("");
  const [errMessage, setErrMessage] = useState("");
  const [success, setSuccess] = useState(false);

  useEffect(() => {
    userRef.current.focus();
  }, []);

  useEffect(() => {
    setErrMessage("");
  }, [user, password]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log(user, password);
    setUser("");
    setPassword("");
    setSuccess(true);
  };
  return (
    <>
      {success ? (
        <section>
          <ContactList />
        </section>
      ) : (
        <section className="auth">
          <p
            ref={errRef}
            className={errMessage ? "errmsg" : "offscreen"}
            aria-live="assertive"
          >
            {errMessage}
          </p>
          <form onSubmit={handleSubmit}>
            <h1>Log In</h1>

            <div className="login">
              <label htmlFor="username">Username:</label>
              <input
                type="text"
                id="username"
                ref={userRef}
                autoComplete="off"
                onChange={(e) => setUser(e.target.value)}
                value={user}
                required
              />
            </div>
            <div className="login">
              <label htmlFor="password">Password:</label>
              <input
                type="password"
                id="Password"
                onChange={(e) => setPassword(e.target.value)}
                value={password}
                required
              />

              <input type="checkbox" />
              <span>Remember me</span>
              <Link to="/contacts/forgot-password">Forget password</Link>
              <button className="actions">Login</button>
            </div>
            <p>
              Needed an account? <br />
              <span className="line">
                <Link to="/contacts/Signup">Sign up</Link>
              </span>
            </p>
          </form>
        </section>
      )}
    </>
  );
};

export default RegisterPage;
