import React, { useState, useEffect, useCallback } from "react";

import ContactsList from "./components/ContactsList";
import AddContact from "./components/AddContact";
import "./App.css";

const App = (props) => {
  const [Contacts, setContacts] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchContactsHandler = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch(
        "http://stack-api-server.herokuapp.com/contact/add"
      );
      if (!response.ok) {
        throw new Error("Something went wrong!");
      }

      const data = await response.json();

      const transformedContacts = data.results.map((contactData) => {
        return {
          id: contactData.id,
          name: contactData.name,
          favorite: contactData.favorite,
          number: contactData.number,
        };
      });
      setContacts(transformedContacts);
    } catch (error) {
      setError(error.message);
    }
    setIsLoading(false);
  }, []);

  useEffect(() => {
    fetchContactsHandler();
  }, [fetchContactsHandler]);

  function addContactHandler(contact) {
    console.log(contact);
  }

  let content = <p>Found no Contact.</p>;

  if (Contacts.length > 0) {
    content = <ContactsList Contacts={Contacts} />;
  }

  if (error) {
    content = <p>{error}</p>;
  }

  if (isLoading) {
    content = <p>Loading...</p>;
  }

  return (
    <React.Fragment>
      <section>
        <AddContact onAddContact={addContactHandler} />
      </section>
      <section>
        <button onClick={fetchContactsHandler}>Fetch Movies</button>
      </section>
      <section>{content}</section>
    </React.Fragment>
  );
};

export default App;
